import React, { useState } from 'react';
import logo from "../assets/logo.png";
import { Divider } from 'primereact/divider';
import { TabView, TabPanel } from 'primereact/tabview';
import { Button } from 'primereact/button';
import FoodCard from '../widgets/FoodCard'; // Import the FoodCard component

const categories = [
  { id: 1, name: 'SUELTOS', subcategories: ["Maiz", "Harina"] },
  { id: 2, name: 'ESPECIALES', subcategories: ["Subcategory1", "Subcategory2"] },
];

// Define food items for each subcategory
const foodItems = {
  "Maiz": [
    { title: "Campechanas", oldPrice: "$297", newPrice: "$198", image: "campechana", description: "3 x 2" },
    // Add more food items for "Maiz" subcategory if needed
  ],
  "Harina": [
    { title: "Prueba2", oldPrice: "$297", newPrice: "$198", image: "campechana", description: "2 x 1" },
    { title: "Prueba3", oldPrice: "$297", newPrice: "$198", image: "campechana", description: "2 x 1" },
    // Add more food items for "Harina" subcategory if needed
  ],
  // Define food items for other subcategories as required
};

// ErrorBoundary component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // You can log the error to an error reporting service
    console.error("Error caught by error boundary:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      // You can render any custom fallback UI
      return <div>Something went wrong.</div>;
    }

    return this.props.children;
  }
}

function Menu() {
  const [activeIndex, setActiveIndex] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]); // Start with the first category

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setActiveIndex(0); // Set activeIndex to 0 to select the first subcategory tab
  };

  return (
    <ErrorBoundary>
      <div style={{ backgroundColor: "var(--background)" }}>
        <img src={logo} alt="Logo" className="w-8rem h-auto px-4 py-2" />
        <div className='overflow-x-auto overflow-y-hidden white-space-nowrap py-2 px-3 mx-1' style={{ WebkitOverflowScrolling: 'touch', scrollbarWidth: 'none', msOverflowStyle: 'none' }}>
          {categories.map((category) => (
            <Button
              key={category.id}
              severity={selectedCategory.id === category.id ? 'danger' : 'secondary'}
              rounded
              className={selectedCategory.id === category.id ? 'mr-2' : 'bg-gray-400 opacity-30 mr-2'}
              onClick={() => handleCategorySelect(category)}
            >
              <h5 className="font-normal m-2 center" style={{ color: 'var(--white)' }}>{category.name}</h5>
            </Button>
          ))}
        </div>
        <div className="mx-4 mb-2">
          <Divider className="h-2" />
        </div>
        <div className="ml-4 mb-4 bg-secondary w-100">
          <TabView activeIndex={activeIndex} onTabChange={(e) => setActiveIndex(e.index)}>
            {selectedCategory.subcategories.map((subcategory, index) => (
              <TabPanel key={index} header={subcategory} className='pr-5' style={{ backgroundColor: "var(--background)" }}>
                <div className='overflow-y-auto max max-h-30rem mt-2'>
                  {/* Map through food items of the selected subcategory */}
                  {foodItems[subcategory].map((foodItem, index) => (
                    <div className='pb-3' key={index}>
                      <FoodCard
                        title={foodItem.title}
                        oldPrice={foodItem.oldPrice}
                        newPrice={foodItem.newPrice}
                        description={foodItem.description}
                      />
                    </div>
                  ))}
                </div>
              </TabPanel>
            ))}
          </TabView>
        </div>
      </div>
    </ErrorBoundary>
  );
}

export default Menu;
