import React, { useState } from 'react';
import logo from "../assets/logo.png";
import { Divider } from 'primereact/divider';
import { TabView, TabPanel } from 'primereact/tabview';
import { Button } from 'primereact/button';
import FoodCard from '../widgets/FoodCard'; // Import the FoodCard component

const categories = [
  { id: 1, name: 'SUELTOS', subcategories: ["Maiz", "Harina"] },
  { id: 2, name: 'ESPECIALES', subcategories: ["Subcategory1", "Subcategory2"] },
];

function Menu() {
  const [activeIndex, setActiveIndex] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]); // Start with the first category

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div style={{ backgroundColor: "var(--background" }}>
      <img src={logo} alt="Logo" className="w-8rem h-auto p-4" />
      <div className='overflow-x-auto overflow-y-hidden white-space-nowrap py-2 px-3 mx-1' style={{ WebkitOverflowScrolling: 'touch', scrollbarWidth: 'none', msOverflowStyle: 'none' }}>
        {categories.map((category) => (
          <Button
            key={category.id}
            severity={selectedCategory.id === category.id ? 'danger' : 'secondary'}
            rounded
            className={selectedCategory.id === category.id ? 'mr-2' : 'bg-gray-400 opacity-30 mr-2'}
            onClick={() => handleCategorySelect(category)}
          >
            <h5 className="font-normal m-2 center" style={{ color: 'var(--white)' }}>{category.name}</h5>
          </Button>
        ))}
      </div>
      <div className="mx-4 my-2">
        <Divider className="h-2" />
      </div>
      <div className="mx-4 my-4">
        <TabView  activeIndex={activeIndex} onTabChange={(e) => setActiveIndex(e.index)} panelContainerClassName="" panelContainerStyle="">
          {selectedCategory.subcategories.map((subcategory, index) => (
            <TabPanel key={index} header={subcategory} className='pr-5' style={{ backgroundColor: "var(--background" }}>
              <div>
                <div className='pt-3'>
                  <FoodCard title="Example Title" oldPrice="$10" newPrice="$8" description="Example Description" />
                </div>
                <div className='pt-3'>
                  <FoodCard title="Example Title" oldPrice="$10" newPrice="$8" description="Example Description" />
                </div>
                <div className='pt-3'>
                  <FoodCard title="Example Title" oldPrice="$10" newPrice="$8" description="Example Description" />
                </div>
                <div className='pt-3'>
                  <FoodCard title="Example Title" oldPrice="$10" newPrice="$8" description="Example Description" />
                </div>

              </div>

            </TabPanel>
          ))}
        </TabView>
      </div>
    </div>
  );
}

export default Menu;
