import React from "react";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import backgroundImage from "../assets/BackgroundImage.png"; 
import logo from "../assets/logo.png"; 
import campechana from "../assets/campechana.png"; 

const array = ["hola", "hola2", "hola3"];

function Home() {
  return (
    <Box
      sx={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        minHeight: "100vh",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <>
        {array.map((element) => {
          return <Button variant="text">{element}</Button>;
        })}

        <Grid container spacing={2}>
          <Grid item xs={3}></Grid>
          <Grid item xs={6}>
            <Paper sx={{ borderRadius: "25px", backgroundColor: "black", display: "flex", justifyContent: "center", alignItems: "center" }} elevation={5}>
              <img src={logo} alt="logo" style={{ width: "80%", height: "auto" , padding:"1rem"}} />
            </Paper>
          </Grid>
          <Grid item xs={3}></Grid>
        </Grid>

        <Grid container spacing={2}>
          <Grid item xs={3}>
            <h1>Promociones</h1>
          </Grid>
          
          <Grid item xs={3}>
            <Paper sx={{
              borderRadius: "25px",
              backgroundColor: "black",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              position: "relative",
              padding: "1rem"
            }} elevation={5}>
              <div style={{ width: "50%", height: "100%", backgroundColor: "red", position: "absolute", left: 0 }}></div>
              <div style={{ width: "50%", height: "100%", display: "flex", flexDirection: "column", justifyContent: "center", paddingLeft: "50%" }}>
                <Typography variant="h6" style={{ color: "white", marginBottom: "1rem" }}>Promotion Title</Typography>
                <Typography variant="body1" style={{ color: "white" }}>Description of the promotion goes here...</Typography>
              </div>
            </Paper>
          </Grid>
        </Grid>

      </>
    </Box>
  );
}

export default Home;
