import React, { useState } from 'react';
import logo from "../assets/logo.png";
import { Divider } from 'primereact/divider';
import { TabView, TabPanel } from 'primereact/tabview';
import { Button } from 'primereact/button';
import ImageList from "@mui/material/ImageList";

const categories = [
  { thumbnail: { uri: "https://lorempixel.com/200/200/animals", name: "Tacos" } },
  { thumbnail: { uri: "https://lorempixel.com/200/200/city", name: "Burritos" } },
  { thumbnail: { uri: "https://lorempixel.com/200/200/city", name: "Enchiladas" } },
  { thumbnail: { uri: "https://lorempixel.com/200/200/city", name: "Quesadillas" } },
  { thumbnail: { uri: "https://lorempixel.com/200/200/nature", name: "Tamales" } },
  { thumbnail: { uri: "https://lorempixel.com/200/200/cats", name: "Guacamole" } },
  { thumbnail: { uri: "https://lorempixel.com/200/200/cats", name: "Salsa" } },
  { thumbnail: { uri: "https://lorempixel.com/200/200/cats", name: "Churros" } }
];

function Menu() {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const selectCategory = (categoryName) => {
    setSelectedCategory(categoryName);
  };

  return (
    <div style={{ backgroundColor: "var(--background)" }}>
      <div>
        <img src={logo} alt="Logo" className="w-8rem h-auto p-4" />
        <ImageList
          sx={{
            gridAutoFlow: "column",
            gridTemplateColumns: "repeat(auto-fit, minmax(160px,1fr)) !important",
            gridAutoColumns: "minmax(160px, 1fr)"
          }}
        >
          {categories.map((category, index) => (
            <div className='py-2' key={index}>
              <Button
                severity={selectedCategory === category.thumbnail.name ? "danger" : "secondary"}
                rounded
                className={`p-2 white-space-nowrap ${selectedCategory === category.thumbnail.name ? "" : "bg-gray-400 opacity-30"}`}
                onClick={() => selectCategory(category.thumbnail.name)}
              >
                <h5 className="font-normal" style={{ color: 'var(--white)' }}>{category.thumbnail.name}</h5>
              </Button>
            </div>
          ))}
        </ImageList>

        <div className="mx-4 my-2">
          <Divider className="h-2" />
        </div>

        <div className="mx-4 my-4">
          <TabView className='' activeIndex={0} style={{ backgroundColor: "var(--background)" }}>
            {categories.map((category, index) => (
              <TabPanel key={index} header={category.thumbnail.name} className='pr-5'>
                <p className="m-0">
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                  Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
                  consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                  Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </p>
              </TabPanel>
            ))}
          </TabView>
        </div>
      </div>
    </div>
  );
}

export default Menu;
