import React from "react";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import backgroundImage from "../assets/BackgroundImage.png";
import logo from "../assets/logo.png";
import campechana from "../assets/campechana.png";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import FavoriteIcon from '@mui/icons-material/Favorite';

const array = ["hola", "hola2", "hola3"];

const sales = [
  {
    title: "Campechanas",
    oldPrice: "$297",
    newPrice: "$198",
    image: campechana,
    description: "3 x 2",
  },
  // Add more sales objects as needed
];

function Home() {
  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 0, // Ensure content is above pseudo-element
        '&::before': {
          content: '""',
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)", // Adjust opacity here
          zIndex: -1,
        },
      }}
    >
      <>
        {array.map((element, index) => {
          return <Button key={index} variant="text">{element}</Button>;
        })}

        <Grid container spacing={2}>
          <Grid item lg={5} md={4} xs={3}></Grid>
          <Grid item lg={2} md={4} xs={6}>
            <Card sx={{ borderRadius: "1rem", backgroundColor: "var(--black)", display: "flex", justifyContent: "center", alignItems: "center" }} elevation={5}>
              <img src={logo} alt="Logo" style={{ width: "80%", height: "auto", padding: "1rem" }} />
            </Card>
          </Grid>
          <Grid item lg={5} md={4} xs={3}></Grid>
        </Grid>

        <h1 className="font-bold" style={{ color: 'var(--white)' }}>Promociones</h1>

        

        <div style={{ width: "90%", margin: "0 auto", marginTop: "100px" }}>
          <Slider
            dots={true}
            infinite={true}
            speed={800}
            autoplay={true}
            autoplaySpeed={2000}
            slidesToShow={4}
            slidesToScroll={4}
            responsive={[
              {
                breakpoint: 1024,
                settings: {
                  slidesToShow: 3,
                  slidesToScroll: 3,
                  infinite: true,
                  dots: true
                }
              },
              {
                breakpoint: 600,
                settings: {
                  slidesToShow: 2,
                  slidesToScroll: 2
                }
              },
              {
                breakpoint: 480,
                settings: {
                  slidesToShow: 1,
                  slidesToScroll: 1
                }
              }
            ]}
          >
            {sales.map((sale, index) => (
          <Card key={index} sx={{ borderRadius: "1rem", backgroundColor: "var(--black)", position: "relative", maxWidth: "24rem", maxHeight: "12rem", display: "flex" }} elevation={5}>
            <div style={{ width: "50%", position: "relative", overflow: "hidden", display: "flex", flexDirection: "column", }}>
              <div style={{ height: "100%", flex: 1, backgroundColor: "var(--red)", justifyContent: "center", alignItems: "center", display: "flex", paddingLeft: "1rem", paddingRight: "1rem" }}>
                <Grid container justifyContent="center" alignItems="center" spacing={0}>
                  <Grid item xs={12}>
                    <h3 className="font-extrabold" style={{ color: 'var(--white)', fontSize: '3rem' }}>{sale.description}</h3>
                  </Grid>
                  <Grid item xs={12}>
                    <h3 className="font-bold" style={{ color: 'var(--black)' }}>{sale.title}</h3>
                  </Grid>
                  <Grid item xs={12}>
                    <h5 className="font-medium" style={{ color: 'var(--black)' }}> De {sale.oldPrice} a</h5>
                  </Grid>
                  <Grid item xs={12}>
                    <h2 className="font-extrabold" style={{ color: 'var(--black)', fontSize: '3rem' }}> {sale.newPrice}</h2>
                  </Grid>
                </Grid>
              </div>
            </div>
            
          </Card>
        ))}
          </Slider>
        </div>

      </>
    </Box>
  );
}

export default Home;
