import React, { useState } from 'react';
import logo from "../assets/logo.png";
import { Divider } from 'primereact/divider';
import { TabView, TabPanel } from 'primereact/tabview';
import { Button } from 'primereact/button';
import ImageList from "@mui/material/ImageList";
import ImageListItem from "@mui/material/ImageListItem";
import ImageListItemBar from "@mui/material/ImageListItemBar";

const categories = [
  { id: 1, name: "Maiz" },
  { id: 2, name: "Harina" },
  // Add more categories as needed
];

function Menu() {
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div style={{ backgroundColor: "var(--background)" }}>
      <div>
        <img src={logo} alt="Logo" className="w-8rem h-auto p-4" />

        <ImageList
          sx={{
            gridAutoFlow: "column",
            gridTemplateColumns: "repeat(auto-fit, minmax(160px,1fr)) !important",
            gridAutoColumns: "minmax(160px, 1fr)"
          }}
        >
          {categories.map((category) => (
            <div key={category.id}>
              <Button key={category.id}
                onClick={() => handleCategoryClick(category)}
                className={`font-normal m-2 white-space-nowrap ${
                  selectedCategory.id === category.id ? 'bg-primary' : 'bg-gray-400'
                }`}
                style={{ color: 'var(--white)' }}
              >
                {category.name}
              </Button>
            </div>
          ))}
        </ImageList>

        <div className="mx-4 my-2">
          <Divider className="h-2" />
        </div>

        <div className="mx-4 my-4">
          <TabView className='' activeIndex={selectedCategory.id - 1} onTabChange={(e) => setSelectedCategory(categories[e.index])} style={{ backgroundColor: "var(--background)" }}>
            {categories.map((category) => (
              <TabPanel key={category.id} header={category.name}>
                <p className="m-0">
                  {/* Placeholder content */}
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                  Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
                  consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                  Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </p>
              </TabPanel>
            ))}
          </TabView>
        </div>
      </div>
    </div>
  );
}

export default Menu;
